    <mat-form-field>
        <mat-label>Search</mat-label>
        <input matInput (keyup)="applyFilter($event)" placeholder="Ex. john" #input>
    </mat-form-field>

    <div class="table-content">
        <table mat-table [dataSource]="dataSource" matSort>

            <ng-container matColumnDef="brandName">
                <th mat-header-cell *matHeaderCellDef mat-sort-header> Brand Name </th>
                <td mat-cell *matCellDef="let row"> {{row.brandName}} </td>
            </ng-container>

            <ng-container matColumnDef="size" >
                <th mat-header-cell *matHeaderCellDef mat-sort-header> Size </th>
                <td mat-cell *matCellDef="let row"> {{row.size}} </td>
            </ng-container>

            <!-- Name Column -->
            <ng-container matColumnDef="pattern">
                <th mat-header-cell *matHeaderCellDef mat-sort-header> Pattern </th>
                <td mat-cell *matCellDef="let row"> {{row.pattern}} </td>
            </ng-container>

            <ng-container matColumnDef="price">
                <th mat-header-cell *matHeaderCellDef mat-sort-header> Price </th>
                <td mat-cell *matCellDef="let row"> {{row.price}} </td>
            </ng-container>
            <ng-container matColumnDef="inStock" >
                <th mat-header-cell *matHeaderCellDef mat-sort-header> Quantity </th>
                <td mat-cell f *matCellDef="let row"> {{row.quantity}} </td>
            </ng-container>
            <ng-container matColumnDef="number_of_items">
                <th mat-header-cell *matHeaderCellDef mat-sort-header> Number of items </th>
                <td mat-cell *matCellDef="let row">
                    <input type="number" class="inputText" [(ngModel)]="row.number_of_items"/>
                </td>
            </ng-container>
            <ng-container matColumnDef="add_to_order">
                <th mat-header-cell *matHeaderCellDef mat-sort-header>  </th>
                <td mat-cell *matCellDef="let row">
                    <button (click)="addToOrder(row)" type="submit" class="buttonStyle">
                        <span>Add to Order</span>
                    </button>

                </td>

            </ng-container>

            <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
            <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>

            <tr class="mat-row" *matNoDataRow>
                <td class="mat-cell" colspan="4">No data matching the filter "{{input.value}}"</td>
            </tr>
        </table>

        <mat-paginator [pageSizeOptions]="[10, 25, 100]" aria-label="Select page of users"></mat-paginator>
    </div>
    <!-- </form> -->
</div>
