import { CommonModule } from '@angular/common';
import { Component, OnInit, ViewChild, Inject } from '@angular/core';
import { MatButtonModule } from '@angular/material/button';
import { MAT_DIALOG_DATA, MatDialog, MatDialogModule } from '@angular/material/dialog';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatIconModule } from '@angular/material/icon';
import { MatInputModule } from '@angular/material/input';
import { MatPaginator, MatPaginatorModule } from '@angular/material/paginator';
import { MatSort, MatSortModule } from '@angular/material/sort';
import { MatTableDataSource, MatTableModule } from '@angular/material/table';
import { MatToolbarModule } from '@angular/material/toolbar';
import { EmployeeService } from 'src/app/services/employee.service';
import { EmployeeEditAddComponent } from '../employee-edit-add/employee-edit-add.component';
import { MatSnackBarModule } from '@angular/material/snack-bar';
import { CoreService } from 'src/app/core/core.service';
import { FormBuilder, FormControl, FormGroup, FormsModule, Validators, ReactiveFormsModule } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import {RouterModule} from '@angular/router';
import { MatTab, MatTabsModule } from '@angular/material/tabs';


@Component({
  selector: 'app-product-listing',
  templateUrl: './product-listing.component.html',
  styleUrls: ['./product-listing.component.css'],
  imports: [
    RouterModule,
    CommonModule,
    MatToolbarModule,
    MatButtonModule,
    MatIconModule,
    MatDialogModule,
    MatTableModule,
    MatSortModule,
    MatFormFieldModule,
    MatInputModule,
    MatPaginatorModule,
    MatIconModule,
    ReactiveFormsModule,
    MatButtonModule,
    MatSnackBarModule,
    FormsModule,
    MatTabsModule
  ],
  standalone: true,
})

// export interface productListing{
//   tires: [], 
//   rims: []
// }

export class ProductListingComponent {
  @ViewChild(MatPaginator) paginator!: MatPaginator;
  @ViewChild(MatSort) sort!: MatSort;
  productListing: any = {tires:[], rims:[]};
  productForm: any = FormGroup;
  private sub: PushSubscription; 
  id:string;

  constructor(
    private dialog: MatDialog,
    private fb: FormBuilder,
    private router: Router,
    private employeeService: EmployeeService,
    private snackbar: CoreService,
    private route: ActivatedRoute
    // @Inject(MAT_DIALOG_DATA) public data: any
  ) {}

  ngOnInit(): void {
     this.route.params.subscribe(params => {
      console.log("params", params);
      this.id = params['userId'];
    })
      this.getTiresList();
      this.getRimsList();
    // }
  }

  routeWithoutTaxInvoice(): void {
    this.router.navigateByUrl('withoutTax', { state: {items: this.productListing }});
  }

  routeToTaxInvoiced(): void {
    this.router.navigateByUrl('checkout', { state: {items: this.productListing }});
  }

  displayedColumns: string[] = [
    'brandName',
    'size',
    'pattern',
    'price',
    'inStock',
    'number_of_items',
    'add_to_order',
  ];

  dataSource!: MatTableDataSource<any>;
  dataSourceRims!: MatTableDataSource<any>;

  applyFilterRims(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.dataSourceRims.filter = filterValue.trim().toLowerCase();

    if (this.dataSourceRims.paginator) {
      this.dataSourceRims.paginator.firstPage();
    }
  }

  getRimsList(): void {
     this.employeeService.getAllRims().subscribe({
       next: (res) => {
       this.dataSourceRims = new MatTableDataSource(res.data);
        this.dataSourceRims.sort = this.sort;
        this.dataSourceRims.paginator = this.paginator;
       },
       error: (err: any) => {},
     });
  }
  addToOrderRims(row:any) {
    console.log(row, "row");
   if(row.quantity < row.number_of_items){
     this.snackbar.openSnackBar('Number of items should always be less than or equals to Quantity', 'Done');
   }else if(row.number_of_items <= 0 || row.number_of_items == null){
     this.snackbar.openSnackBar('Please input right number of items', 'Done');
   }else{  
     localStorage.clear();
     const index = this.productListing['rims'].findIndex((element:any) => element._id == row._id);
     if (index !== -1) {
       this.productListing.rims.splice(index, 1);
     }
     this.productListing.rims.push(row);
     this.snackbar.openSnackBar('Products has been added', 'Done');
     console.log("products listing", this.productListing);
   }
 }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.dataSource.filter = filterValue.trim().toLowerCase();

    if (this.dataSource.paginator) {
      this.dataSource.paginator.firstPage();
    }
  }

  getTiresList(): void {
     this.employeeService.getTiresDetails().subscribe({
       next: (res) => {
       this.dataSource = new MatTableDataSource(res.data);
        this.dataSource.sort = this.sort;
        this.dataSource.paginator = this.paginator;
       },
       error: (err: any) => {},
     });
  }

  addToOrder(row:any) {
     console.log(row, "row");
    if(row.quantity < row.number_of_items){
      this.snackbar.openSnackBar('Number of items should always be less than or equals to Quantity', 'Done');
    }else if(row.number_of_items <= 0 || row.number_of_items == null){
      this.snackbar.openSnackBar('Please input right number of items', 'Done');
    }else{  
      localStorage.clear();
      console.log(this.productListing);
      const index = this.productListing.tires.findIndex((element:any) => element._id == row._id);
      if (index !== -1) {
        this.productListing.tires.splice(index, 1);
      }
      this.productListing.tires.push(row);
      this.snackbar.openSnackBar('Products has been added', 'Done');
      console.log("products listing", this.productListing);

    }
  }

  editEmployeeForm(data: any): void {
    const dialogRef = this.dialog.open(EmployeeEditAddComponent, {
      data,
    });
    dialogRef.afterClosed().subscribe({
      next: (value) => {
        if (value) {
          this.getTiresList();
        }
      },
    });
  }
}
<mat-toolbar color="primary">

    <div [routerLink]="['']">SHAH G RIM & TIRES</div>
    <span class="example-spacer"></span>
    <button mat-raised-button (click)="routeWithoutTaxInvoice()" style="margin-right: 5px;">
       Without Tax Invoice
    </button>
    <button mat-raised-button (click)="routeToTaxInvoiced()">
       Taxed Invoice
    </button>
</mat-toolbar>
<!-- ! table component -->
<div class="table-container">
  <mat-tab-group>
    <mat-tab label="Tires Section">
      <mat-form-field>
        <mat-label>Search</mat-label>
        <input matInput (keyup)="applyFilter($event)" placeholder="Ex. john" #input>
    </mat-form-field>
      <div class="table-content">
        <table mat-table [dataSource]="dataSource" matSort>

            <ng-container matColumnDef="brandName">
                <th mat-header-cell *matHeaderCellDef mat-sort-header> Brand Name </th>
                <td mat-cell *matCellDef="let row"> {{row.brandName}} </td>
            </ng-container>

            <ng-container matColumnDef="size" >
                <th mat-header-cell *matHeaderCellDef mat-sort-header> Size </th>
                <td mat-cell *matCellDef="let row"> {{row.size}} </td>
            </ng-container>

            <!-- Name Column -->
            <ng-container matColumnDef="pattern">
                <th mat-header-cell *matHeaderCellDef mat-sort-header> Pattern </th>
                <td mat-cell *matCellDef="let row"> {{row.pattern}} </td>
            </ng-container>

            <ng-container matColumnDef="price">
                <th mat-header-cell *matHeaderCellDef mat-sort-header> Price </th>
                <td mat-cell *matCellDef="let row"> {{row.price}} </td>
            </ng-container>
            <ng-container matColumnDef="inStock" >
                <th mat-header-cell *matHeaderCellDef mat-sort-header> Quantity </th>
                <td mat-cell f *matCellDef="let row"> {{row.quantity}} </td>
            </ng-container>
            <ng-container matColumnDef="number_of_items">
                <th mat-header-cell *matHeaderCellDef mat-sort-header> Number of items </th>
                <td mat-cell *matCellDef="let row">
                    <input type="number" class="inputText" [(ngModel)]="row.number_of_items"/>
                </td>
            </ng-container>
            <ng-container matColumnDef="add_to_order">
                <th mat-header-cell *matHeaderCellDef mat-sort-header>  </th>
                <td mat-cell *matCellDef="let row">
                    <button (click)="addToOrder(row)" type="submit" class="buttonStyle">
                        <span>Add to Order</span>
                    </button>

                </td>

            </ng-container>

            <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
            <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>

            <tr class="mat-row" *matNoDataRow>
                <td class="mat-cell" colspan="4">No data matching the filter "{{input.value}}"</td>
            </tr>
        </table>

        <mat-paginator [pageSizeOptions]="[10, 25, 100]" aria-label="Select page of users"></mat-paginator>
    </div>
    </mat-tab>
    <mat-tab label="Rims Section">
      <mat-form-field>
        <mat-label>Search Rims</mat-label>
        <input matInput (keyup)="applyFilterRims($event)" placeholder="Ex. john" #input>
    </mat-form-field>

    <div class="table-content">
        <table mat-table [dataSource]="dataSourceRims" matSort>

            <ng-container matColumnDef="brandName">
                <th mat-header-cell *matHeaderCellDef mat-sort-header> Brand Name </th>
                <td mat-cell *matCellDef="let row"> {{row.brandName}} </td>
            </ng-container>

            <ng-container matColumnDef="size" >
                <th mat-header-cell *matHeaderCellDef mat-sort-header> Size </th>
                <td mat-cell *matCellDef="let row"> {{row.size}} </td>
            </ng-container>

            <!-- Name Column -->
            <ng-container matColumnDef="pattern">
                <th mat-header-cell *matHeaderCellDef mat-sort-header> Pattern </th>
                <td mat-cell *matCellDef="let row"> {{row.pattern}} </td>
            </ng-container>

            <ng-container matColumnDef="price">
                <th mat-header-cell *matHeaderCellDef mat-sort-header> Price </th>
                <td mat-cell *matCellDef="let row"> {{row.price}} </td>
            </ng-container>
            <ng-container matColumnDef="inStock" >
                <th mat-header-cell *matHeaderCellDef mat-sort-header> Quantity </th>
                <td mat-cell f *matCellDef="let row"> {{row.quantity}} </td>
            </ng-container>
            <ng-container matColumnDef="number_of_items">
                <th mat-header-cell *matHeaderCellDef mat-sort-header> Number of items </th>
                <td mat-cell *matCellDef="let row">
                    <input type="number" class="inputText" [(ngModel)]="row.number_of_items"/>
                </td>
            </ng-container>
            <ng-container matColumnDef="add_to_order">
                <th mat-header-cell *matHeaderCellDef mat-sort-header>  </th>
                <td mat-cell *matCellDef="let row">
                    <button (click)="addToOrderRims(row)" type="submit" class="buttonStyle">
                        <span>Add to Order</span>
                    </button>

                </td>

            </ng-container>

            <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
            <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>

            <tr class="mat-row" *matNoDataRow>
                <td class="mat-cell" colspan="4">No data matching the filter "{{input.value}}"</td>
            </tr>
        </table>

        <mat-paginator [pageSizeOptions]="[10, 25, 100]" aria-label="Select page of users"></mat-paginator>
    </div>
    </mat-tab>
  </mat-tab-group>
