    <div [routerLink]="['']">SHAH G RIM & TIRES</div>
    <span class="example-spacer"></span>
    <!-- <button mat-raised-button (click)="addEmployeeForm()">
        ADD RIMS
    </button> -->
</mat-toolbar>
<!-- ! table component -->
<div class="table-container">
    <div class="col-md-6">
        <!-- <div class="search-dropdown-container"> -->
            <!-- Search input -->


            <input class="search-input" type="text" (keyup)="applyFilter($event)" placeholder="Search..." #input/>

            <!-- Dropdown select -->
            <div class="dropdown-select" style="float: right;">
                <select (change)="onDropdownChange($event)" style="border: none;">
                    <option value="week" style="padding: 10px;">This Week</option>
                    <option value="today">Today</option>
                    <option value="month">This Month</option>
                    <option value="year">This Year</option>
                  </select>
          </div>
    </div>
    <!-- <div class="col-md-6">

    </div> -->


    <div class="table-content">
        <table mat-table [dataSource]="dataSource" matSort>

            <ng-container matColumnDef="invoiceNumber">
                <th mat-header-cell *matHeaderCellDef mat-sort-header> Invoice Number </th>
                <td mat-cell *matCellDef="let row"> {{row.invoiceNumber}} </td>
            </ng-container>

            <ng-container matColumnDef="customerName">
                <th mat-header-cell *matHeaderCellDef mat-sort-header> Customer Name </th>
                <td mat-cell *matCellDef="let row"> {{row.customerName}} </td>
            </ng-container>

            <!-- Name Column -->
            <ng-container matColumnDef="invoiceType">
                <th mat-header-cell *matHeaderCellDef mat-sort-header> Invoice Type </th>
                <td mat-cell *matCellDef="let row"> {{row.invoiceType}} </td>
            </ng-container>

            <ng-container matColumnDef="totalAmount">
                <th mat-header-cell *matHeaderCellDef mat-sort-header> Total Amount </th>
