import { CommonModule } from '@angular/common';
import { Component, Inject, OnInit, ViewChild } from '@angular/core';
import { MatButtonModule } from '@angular/material/button';
import {
  MAT_DIALOG_DATA,
  MatDialogModule,
  MatDialogRef,
} from '@angular/material/dialog';
import { FormControl, FormGroup, ReactiveFormsModule, Validators } from '@angular/forms';
import { FormBuilder } from '@angular/forms';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatRadioModule } from '@angular/material/radio';
import { EmployeeService } from 'src/app/services/employee.service';
import { DialogRef } from '@angular/cdk/dialog';
import { MatSnackBarModule } from '@angular/material/snack-bar';
import { CoreService } from 'src/app/core/core.service';
import { MatPaginator } from '@angular/material/paginator';
import { MatSort } from '@angular/material/sort';

@Component({
  selector: 'app-employee-edit-add',
  standalone: true,
  imports: [
    CommonModule,
    MatDialogModule,
    MatButtonModule,
    ReactiveFormsModule,
    MatRadioModule,
    MatSnackBarModule,
    MatFormFieldModule,
  ],
  templateUrl: './employee-edit-add.component.html',
  styleUrls: ['./employee-edit-add.component.css'],
})
export class EmployeeEditAddComponent implements OnInit {
   @ViewChild(MatPaginator) paginator!: MatPaginator;
   @ViewChild(MatSort) sort!: MatSort;
  tireForm: any = FormGroup;
  typeSelected = "";
  brandList:any  = [];

  constructor(
    private fb: FormBuilder,
    private employeeService: EmployeeService,
    private dialogRef: MatDialogRef<EmployeeEditAddComponent>,
    private snackbar: CoreService,
    @Inject(MAT_DIALOG_DATA) public data: any
  ) {}

  ngOnInit(): void {
    this.tireForm = this.fb.group({
      brandName: new FormControl("", [Validators.required]),
      size: new FormControl(null, [Validators.required]),
      pattern: new FormControl(null, [Validators.required]),
      price: new FormControl(null, [Validators.required]),
      quantity: new FormControl(null, Validators.required),
    });

    this.tireForm.patchValue(this.data);
    this.getBrandList();
  }

  typeChange(event:any){
    this.typeSelected =  event.target.value
  }

  
  getBrandList(): void {
    this.employeeService.getBrands().subscribe({
      next: (res) => {
      this.brandList = res.data;
      },
      error: (err: any) => {},
    });
 }

  onFormSubmit() {
    console.log(this.data);
    if (this.tireForm.valid) {
      this.tireForm.value["size"] = this.tireForm.value.size.trim();
      console.log(this.tireForm.value);
      if (this.data) {
        this.tireForm.value["_id"] = this.data._id;
        console.log(this.tireForm.value, "biwbiewrgbeirugb");
        this.employeeService
          .updateTires(this.tireForm.value)
          .subscribe({
            next: (value) => {
              this.dialogRef.close(true);
            },
            error: (err) => {
              console.log(err);
            },
          });
      } else {
        this.employeeService.addTires(this.tireForm.value).subscribe({
          next: (val: any) => {
            this.snackbar.openSnackBar('Tires Added Successfully', 'done');

            this.dialogRef.close(true);
          },
          error: (err: any) => {
            this.snackbar.openSnackBar(err.error.message, 'done');
            console.log(err);
          },
        });
      }
    }
  }
}
<div mat-dialog-title class="text-center">
    <h1 color="primary">
      {{data ? 'Update Tires Form':'Add Tires Form'}}
    </h1>
   
</div>
<form [formGroup]="tireForm" (ngSubmit)="onFormSubmit()" autocomplete="false" novalidate #formDir="ngForm">
    <div mat-dialog-content>
        <div>
          <div class="col-8">
            <div class="form-group">
              <label style="margin-top: 10px; font-size: 18px;">Select Brand name</label>
              <select  (change)="typeChange($event)"  formControlName="brandName" style="margin: 10px; height: 32px;width: 120px; margin-top: 10px;">
                <option *ngFor="let option of brandList" [value]="option.brandName">
                  {{option.brandName}}
                </option>
              </select>
              <div
                *ngIf="tireForm.controls.brandName.invalid && (tireForm.controls.brandName.dirty || tireForm.controls.brandName.touched) && formDir.submitted">
                <span class="text-danger"
                  *ngIf="tireForm.controls.brandName.errors.required">
                  Please enter brand name.
                </span>

              </div>
            </div>
          </div>
            <div class="col-8"  >
                <div class="form-group">
                    <label style="margin-top: 10px; font-size: 18px;">Enter Size</label>
                  <input class="form-control" type="text" matInput placeholder="Size" formControlName="size" style="margin-top: 10px;"
                    maxlength="40" />
                  <div
                    *ngIf="tireForm.controls.size.invalid && (tireForm.controls.size.dirty || tireForm.controls.size.touched) && formDir.submitted">
                    <span class="text-danger"
                      *ngIf="tireForm.controls.size.errors.required">
                      Please enter Size.
                    </span>
                  </div>
                </div>

              </div>
              <br/>
              <div class="col-8"  >
                <div class="form-group">
                    <label style="margin-top: 10px; font-size: 18px;">Enter Price</label>
                  <input class="form-control" type="number" matInput placeholder="Price" formControlName="price" style="margin-top: 10px;"
                    maxlength="40" />
                    <div
                    *ngIf="tireForm.controls.price.invalid && (tireForm.controls.price.dirty || tireForm.controls.price.touched) && formDir.submitted">
                    <span class="text-danger"
                      *ngIf="tireForm.controls.price.errors.required">
                      Please enter Price.
                    </span>
                  </div>
                </div>

              </div>
              <br/>
              <div class="col-8"  >
                <div class="form-group">
                    <label style="margin-top: 10px; font-size: 18px;">Enter Quantity</label>
                  <input class="form-control" type="number" matInput placeholder="Quantity" formControlName="quantity" style="margin-top: 10px;"
                    maxlength="40" />
                  <div
                    *ngIf="tireForm.controls.quantity.invalid && (tireForm.controls.quantity.dirty || tireForm.controls.quantity.touched) && formDir.submitted">
                    <span class="text-danger"
                      *ngIf="tireForm.controls.quantity.errors.required">
                      Please enter Quantity.
                    </span>
                  </div>
                </div>

              </div>
              <br/>
              <div class="col-8">
                <div class="form-group">
                  <label style="margin-top: 10px; font-size: 18px;">Select Pattern</label>
                  <select  (change)="typeChange($event)"  formControlName="pattern" style="margin: 10px; height: 32px;width: 120px; margin-top: 10px;">
                    <option value="Snow">Snow Tires</option>
                    <option value="AllSeason">All Season</option>
                    <option value="AllSeason">All Weather</option>
                  </select>
                  <div
                    *ngIf="tireForm.controls.pattern.invalid && (tireForm.controls.pattern.dirty || tireForm.controls.pattern.touched) && formDir.submitted">
                    <span class="text-danger"
                      *ngIf="tireForm.controls.pattern.errors.required">
                      Please enter pattern type.
                    </span>

                  </div>
                </div>
              </div>
        </div>
    </div>
    <div mat-dialog-actions class="d-flex action-btn">
        <button mat-raised-button type="button" [mat-dialog-close]="false ">Cancel</button>
        <button mat-raised-button color="primary" type="submit">{{data ? 'Update':'Save'}}</button>
    </div>
</form>
